mapper.select:
select
,
(select nickname from cust_user u where u.id = c.create_user) as createUserName,
(select u.id from cust_talk t, cust_user u where u.id = t.create_user and t.id = c.talk_id ) as talkCreateUserId
from cust_comment c


    and status = #{status}


    and talk_id = #{talkId}


    and talkCreateUserId = #{talkCreateUserId}


order by update_time desc
